com.dec.avw.core.ProcessDefinition.source:
process mobileCompany()
version 1;
name "MobileCompany";
forms offerDraft offerDraft "offerDraft",closeCallNotes closeCallNotes "Close Call Notes",feedbackNotes feedbackNotes "Feedback Notes",incomingCustomerCall incomingCustomerCall "incomingCustomerCall",customPlanProposal customPlanProposal "Custom Plan Proposal",incomingCallForm form_incomingCall "incomingCallForm";
application default;
<a1>
begin "start"
   <a2>
   helpdesk!home manuel() "receive customer call";
   <a3>
   !beatrix manuel(incomingCustomerCall) "fill the form";
   <a3_0>
   if UNSPECIFIED then "current plan ?"
      <a3_1>
      !beatrix manuel() "check request";
      <a3_1_1>
      if xpath:"$form_incomingCustomerCall/c_reqCheaper = true" then "is request cheaper plan?"
         <a4>
         !beatrix pm_inform() "inform customer";
         <a5>
         !beatrix manuel() "close call";
         <a6>
         !beatrix manuel(closeCallNotes) "take note";
         <a7>
         !beatrix,closeCallNotes.note,!wong sendTask(closeCallNotes) "send notes to junior sales";
         <a8>
         !wong,closeCallNotes.note demo_receive(closeCallNotes) "receive notes from helpdesk";
         <a9>
         !wong,closeCallNotes.note manuel() "read notes from helpdesk";
         <a10>
         !wong,closeCallNotes.note manuel(closeCallNotes) skipable "add additional notes";
         <a11>
         closeCallNotes.note,!wong,!beatrix sendTask(closeCallNotes) "send to helpdesk";
         <a12>
         !beatrix demo_receive(closeCallNotes) "Receive extended Notes";
         <a36>
         goto a34 "end";
      <a3_0_0_0>
      end "merge";
   else
      <a3_0_1>
      if UNSPECIFIED then "current plan ?"
         <a3_2>
         !beatrix manuel() "check request";
      else
         <a35>
         goto a34 "end";
      end "current plan is BillionaireUnlimited";
      <a3_0_2>
      if xpath:"$form_incomingCustomerCall/c_reqBetter = true" then "is request better plan ?"
         <a13>
         !beatrix,!wallace,closeCallNotes.note sendTask(incomingCustomerCall) "forward request to senior sales";
         <a14>
         !wallace,closeCallNotes.note demo_receive(incomingCustomerCall) "receive notes from helpdesk";
         <a15>
         !wallace manuel(customPlanProposal) "create custom plan proposal";
         <a16>
         !wallace pm_inform() "inform customer";
         <a17>
         !wallace,!beatrix sendTask(customPlanProposal) "send information to helpdesk";
         <a18>
         !beatrix demo_receive(customPlanProposal) "recieve information";
         <a19>
         !beatrix pm_inform(feedbackNotes) "contact customer";
         <a20>
         !beatrix manuel() "close call";
         <a37>
         goto a34 "end";
      <a1000>
      end "merge";
   <a1001>
   end "merge";
   <a21>
   !beatrix,!brody sendTask(incomingCustomerCall) "forward to sales";
   <a22>
   !brody demo_receive(incomingCustomerCall) "receive request from helpdes";
   <a23>
   !brody manuel(offerDraft) "prepare offer draft";
   <a24>
   !brody,!pender sendTask(offerDraft) "forward to senior sales";
   !pender demo_receive(offerDraft) "receive offer draft from junior sales";
   <a25>
   !pender manuel(offerDraft) skipable "change price";
   <a1002>
   if xpath:"$form_offerDraft/newPrice > 100" then "check new price"
      <a100>
      goto a25 "price have to be lower than 100";
   else
      <a26>
      !pender,!brody sendTask(offerDraft) "forward to junior sales";
      <a27>
      !brody demo_receive(offerDraft) "receive offer draft from senior sales";
      <a28>
      !brody pm_inform() "contact customer";
      <a29>
      !brody,!mcfly sendTask(offerDraft) "send data to helpdesk";
      <a30>
      !mcfly demo_receive(offerDraft) "receive data from junior sales";
      <a31>
      !mcfly pm_inform() "contact customer";
      <a32>
      !mcfly manuel(feedbackNotes) "take feedback notes";
      <a33>
      !mcfly manuel() "close call";
   <a1004>
   end "merge";
<a34>
end "end"
